<?phprequire_once 'connect.php';header('Content-Type:' . "application/json");$ArrayGeral = array();$referencias = array();$sql = "SELECT ref FROM registro_de_compras where status= 'Enviado'";$query = $conexao->query($sql);$repetida = "nao";while ($dados = $query->fetch_assoc()) {    foreach ($referencias as $r) {        if ($r == $dados['ref']) {            $repetida = "sim";        }    }    if ($repetida == "nao") {        $referencias[] = $dados['ref'];    } else {        $repetida = "nao";    }}$j = 0;foreach ($referencias as $ref) {        $sql = "SELECT *,produtos.nome AS nomeprod FROM registro_de_compras INNER JOIN produtos ON registro_de_compras.id_produto = produtos.id "            . " WHERE  registro_de_compras.ref = '$ref'";    $query = $conexao->query($sql);    $i = 0;    while ($dados = $query->fetch_assoc()) {        $id_usuario = $dados['id_usuario'];        $data_compra = $dados['data_compra'];        $rastreamento = $dados['rastreamentoCorreios'];        $ArrayGeral[$j]['produtos'][$i]['id_produto'] = $dados['id_produto'];        $ArrayGeral[$j]['produtos'][$i]['quantidade_produto'] = $dados['quant'];        $ArrayGeral[$j]['produtos'][$i]['id_registro'] = $dados['id'];        $ArrayGeral[$j]['produtos'][$i]['nome_produto'] = $dados['nomeprod'];        $ArrayGeral[$j]['produtos'][$i]['altura_produto'] = $dados['altura'];        $ArrayGeral[$j]['produtos'][$i]['largura_produto'] = $dados['largura'];        $ArrayGeral[$j]['produtos'][$i]['cor_produto'] = $dados['cor'];                $ArrayGeral[$j]['produtos'][$i]['comprimento_produto'] = $dados['comprimento'];        $i++;    }    $sql2 = "SELECT * FROM usuario WHERE id = $id_usuario";    $query2 = $conexao->query($sql2);    while ($dadosUsuario = $query2->fetch_assoc()) {        $nomeUsuario = $dadosUsuario['nome'];        $emailUsuario = $dadosUsuario['email'];        $loginUsuario = $dadosUsuario['login'];        $cepUsuario = $dadosUsuario['cep'];        $telUsuario = $dadosUsuario['tel'];        $celUsuario = $dadosUsuario['cell'];        $ruaUsuario = $dadosUsuario['rua'];        $numeroUsuario = $dadosUsuario['numero'];        $complementoUsuario = $dadosUsuario['complemento'];        $bairroUsuario = $dadosUsuario['bairro'];        $cidadeUsuario = $dadosUsuario['cidade'];        $ufUsuario = $dadosUsuario['uf'];    }    $ArrayGeral[$j]['celUsuario'] = $celUsuario;    $ArrayGeral[$j]['rastreamento'] = $rastreamento;    $ArrayGeral[$j]['cidadeUsuario'] = $cidadeUsuario;    $ArrayGeral[$j]['ufUsuario'] = $ufUsuario;    $ArrayGeral[$j]['numeroUsuario'] = $numeroUsuario;    $ArrayGeral[$j]['complementoUsuario'] = $complementoUsuario;    $ArrayGeral[$j]['bairroUsuario'] = $bairroUsuario;    $ArrayGeral[$j]['cepUsuario'] = $cepUsuario;    $ArrayGeral[$j]['ruaUsuario'] = $ruaUsuario;    $ArrayGeral[$j]['nomeUsuario'] = $nomeUsuario;    $ArrayGeral[$j]['telUsuario'] = $telUsuario;    $ArrayGeral[$j]['loginUsuario'] = $loginUsuario;    $ArrayGeral[$j]['emailUsuario'] = $emailUsuario;    $ArrayGeral[$j]['id_usuario'] = $id_usuario;    $ArrayGeral[$j]['ref'] = $ref;    $ArrayGeral[$j]['data_compra'] = $data_compra;    $j++;}echo json_encode($ArrayGeral, JSON_PRETTY_PRINT);